  @Rule
  public ExpectedException thrown = ExpectedException.none();


  @Before
  public void Initialize() {
    impl = new RestApiRequestImpl("12345", "67890", new RequestOptions());
    account = new Account();
    account.setId(5628009L);
  }

  @Test
  public void test() {

    RestApiRequest<List<Balance>> restApiRequest = impl.getBalance(account);
    String str = String.format("/v1/account/accounts/%d/balance", account.getId());
    assertTrue(restApiRequest.request.url().toString().contains(str));
    assertEquals("GET", restApiRequest.request.method());
    assertNotNull(restApiRequest.request.url().queryParameter("Signature"));
  }


  @Test
  public void testResult_Normal() {
    RestApiRequest<List<Balance>> restApiRequest =
        impl.getBalance(account);
    JsonWrapper jsonWrapper = JsonWrapper.parseFromString(data);
    List<Balance> balances = restApiRequest.jsonParser.parseJson(jsonWrapper);
    assertEquals("lun", balances.get(0).getCurrency());
    assertEquals(new BigDecimal("0"), balances.get(0).getBalance());
    assertEquals("trade", balances.get(0).getType().toString().toLowerCase());
  }

  @Test
  public void testResult_Unexpected() {
    RestApiRequest<List<Balance>> restApiRequest =
        impl.getBalance(account);
    JsonWrapper jsonWrapper = JsonWrapper.parseFromString(Errordata);
    thrown.expect(HuobiApiException.class);
    thrown.expectMessage("Get json item field");
    restApiRequest.jsonParser.parseJson(jsonWrapper);
  }
